local m,p,s=peripheral.find("monitor"),0,0;m.setTextScale(1.5)m.setBackgroundColor(colors.black)m.setTextColor(colors.white)function cls()m.clear()end;function txt(x,y,t,c)c=c or colors.white;m.setTextColor(c)m.setCursorPos(x,y)m.write(t)end;function waitTouch()local e,s,x,y=os.pullEvent("monitor_touch")return x,y end;function scene1()cls()txt(2,2,"You wake up lost in darkness...")sleep(2)txt(2,3,"A gentle voice: 'I am with you.'")sleep(2)txt(2,5,"Follow the Light?")txt(2,6,"[ YES ]")txt(2,7,"[ NO ]")while true do _,_,x,y=waitTouch()if y==6 then scene2()break elseif y==7 then cls()txt(2,9,"You stay... but His love remains.")break end end end;function scene2()cls()txt(2,2,"You walk in faith.")sleep(1)txt(2,3,"You see someone crying.")sleep(1)txt(2,5,"Help them?")txt(2,6,"[ HELP ]")txt(2,7,"[ IGNORE ]")while true do _,_,x,y=waitTouch()if y==6 then p=p+1;scene3()break elseif y==7 then scene3()break end end end;function scene3()cls()txt(2,2,"You keep walking...")sleep(1)txt(2,3,"A light shines brighter.")sleep(1)txt(2,5,"God is with you.")txt(2,7,"Faith Points: "..p,colors.lime)txt(2,9,"Touch to end")waitTouch()cls()txt(2,5,"Thanks for playing!")txt(2,6,"Jesus loves you!")end;scene1()
