local m,p=peripheral.find("monitor"),0;m.setTextScale(1.5)m.setBackgroundColor(colors.black)m.setTextColor(colors.white)function cls()m.clear()end;function btn(x,y,t)w=#t txt(x,y,t) return{x=x,y=y,w=w}end;function touched(b,x,y)return y==b.y and x>=b.x and x<=b.x+b.w-1 end;function scene1()cls()txt(2,2,"You wake up lost in darkness...")sleep(2)txt(2,3,"A gentle voice: 'I am with you.'")sleep(2)local byes=btn(2,6,"[ YES ]")local bno=btn(12,6,"[ NO ]")while true do _,_,x,y=os.pullEvent("monitor_touch")if touched(byes,x,y)then scene2()break elseif touched(bno,x,y)then cls()txt(2,8,"You stay... but His love remains.")break end end end;function scene2()cls()txt(2,2,"You walk in faith.")sleep(1)txt(2,3,"You see someone crying.")sleep(1)local bhelp=btn(2,6,"[ HELP ]")local bign=btn(12,6,"[ IGNORE ]")while true do _,_,x,y=os.pullEvent("monitor_touch")if touched(bhelp,x,y)then p=p+1;scene3()break elseif touched(bign,x,y)then scene3()break end end end;function scene3()cls()txt(2,2,"You keep walking...")sleep(1)txt(2,3,"A light shines brighter.")sleep(1)txt(2,5,"God is with you.")txt(2,6,"Faith Points: "..p,colors.lime)txt(2,8,"Touch to end")os.pullEvent("monitor_touch")cls()txt(2,5,"Thanks for playing!")txt(2,6,"Jesus loves you!")end;function txt(x,y,t,c)c=c or colors.white;m.setTextColor(c)m.setCursorPos(x,y)m.write(t)end;scene1()
